import React, { useEffect, useRef } from 'react';
import { Helmet } from 'react-helmet';
import * as THREE from 'three';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader.js';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './model.css';

const Model = () => {
  const mountRef = useRef(null);

  useEffect(() => {
    // Basic Three.js setup
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, mountRef.current.clientWidth / mountRef.current.clientHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(mountRef.current.clientWidth, mountRef.current.clientHeight);
    mountRef.current.appendChild(renderer.domElement);

    // Load the 3D model
    const loader = new GLTFLoader();
    loader.load('public/a_low_poly_house', (gltf) => {
      scene.add(gltf.scene);
    }, undefined, (error) => {
      console.error('An error happened while loading the model:', error);
    });

    // Lighting
    const ambientLight = new THREE.AmbientLight(0x404040, 5);
    scene.add(ambientLight);
    const directionalLight = new THREE.DirectionalLight(0xffffff, 2);
    directionalLight.position.set(1, 1, 1).normalize();
    scene.add(directionalLight);

    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Cleanup on component unmount
    return () => {
      mountRef.current.removeChild(renderer.domElement);
      renderer.dispose();
    };
  }, []);

  return (
    <div className="model-container">
      <Helmet>
        <title>Model - Mobillio Online Store</title>
        <meta property="og:title" content="Model - Mobillio Online Store" />
      </Helmet>
      <Navbar text="Back to Store" />
      <div className="model-viewer" ref={mountRef}></div>
      <Footer />
    </div>
  );
};

export default Model;
