import React, { useEffect, useRef, useState } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

const ThreeDModelViewer = ({ modelUrl }) => {
    const containerRef = useRef(null); // Ref for the container
    const modelRef = useRef(null); // Ref for the model
    const keyControls = useRef({ autoRotate: false, wireframe: false }); // Ref for key controls
    const [showInstructions, setShowInstructions] = useState(true); // State for instructions

    useEffect(() => {
        // Show instructions for 3 seconds
        const timer = setTimeout(() => {
            setShowInstructions(false);
        }, 3000);

        if (!containerRef.current) return;

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.outputColorSpace = THREE.SRGBColorSpace;
        renderer.setSize(containerRef.current.clientWidth, containerRef.current.clientHeight);
        renderer.setClearColor(0x000000);
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.shadowMap.enabled = true;
        renderer.shadowMap.type = THREE.PCFSoftShadowMap;
        containerRef.current.appendChild(renderer.domElement);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(
            45,
            containerRef.current.clientWidth / containerRef.current.clientHeight,
            1,
            1000
        );
        camera.position.set(0, 10, 20);

        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05; // Smoother damping
        controls.enablePan = true; // Enable panning
        controls.enableZoom = true;
        controls.zoomSpeed = 1.0;
        controls.rotateSpeed = 0.8; // Adjusted for smoother rotation
        controls.panSpeed = 0.8; // Adjusted pan speed
        controls.minDistance = 5;
        controls.maxDistance = 50;
        controls.minPolarAngle = 0; // Allow full vertical rotation
        controls.maxPolarAngle = Math.PI; // Allow full vertical rotation
        controls.autoRotate = false;
        controls.target.set(0, 0, 0);

        const spotLight = new THREE.SpotLight(0xffffff, 3000, 200, 0.5, 0.5);
        spotLight.position.set(10, 30, 10);
        spotLight.castShadow = true;
        spotLight.shadow.bias = -0.0001;
        spotLight.shadow.mapSize.width = 2048;
        spotLight.shadow.mapSize.height = 2048;
        scene.add(spotLight);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const loader = new GLTFLoader().setPath('public/a_low_poly_house/');
        loader.load(
            'scene.gltf',
            (gltf) => {
                console.log('loading model');
                const mesh = gltf.scene;
                modelRef.current = mesh;

                const box = new THREE.Box3().setFromObject(mesh);
                const center = box.getCenter(new THREE.Vector3());
                mesh.position.sub(center);

                mesh.traverse((child) => {
                    if (child.isMesh) {
                        child.castShadow = true;
                        child.receiveShadow = true;
                    }
                });

                scene.add(mesh);

                const size = box.getSize(new THREE.Vector3());
                const maxDim = Math.max(size.x, size.y, size.z);
                const fov = camera.fov * (Math.PI / 180);
                let cameraZ = Math.abs(maxDim / Math.tan(fov / 2) * 3);

                camera.position.set(0, maxDim, cameraZ);
                camera.lookAt(0, 0, 0);

                controls.target.set(0, 0, 0);
                controls.update();

                document.getElementById('progress-container').style.display = 'none';
            },
            (xhr) => {
                console.log(`loading ${xhr.loaded / xhr.total * 100}%`);
            },
            (error) => {
                console.error(error);
            }
        );

        // Keyboard controls
        const handleKeyDown = (event) => {
            switch(event.code) {
                case 'Space':
                    keyControls.current.autoRotate = !keyControls.current.autoRotate;
                    controls.autoRotate = keyControls.current.autoRotate;
                    break;
                case 'KeyW':
                    if (modelRef.current) {
                        keyControls.current.wireframe = !keyControls.current.wireframe;
                        modelRef.current.traverse((child) => {
                            if (child.isMesh) {
                                child.material.wireframe = keyControls.current.wireframe;
                            }
                        });
                    }
                    break;
                case 'KeyR':
                    // Reset camera position
                    camera.position.set(0, 10, 20);
                    controls.target.set(0, 0, 0);
                    controls.autoRotate = false;
                    keyControls.current.autoRotate = false;
                    controls.update();
                    break;
            }
        };

        window.addEventListener('keydown', handleKeyDown);

        const handleResize = () => {
            camera.aspect = containerRef.current.clientWidth / containerRef.current.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(containerRef.current.clientWidth, containerRef.current.clientHeight);
            controls.update();
        };

        window.addEventListener('resize', handleResize);

        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };

        animate();

        return () => {
            clearTimeout(timer); // Clear the timer on unmount
            window.removeEventListener('keydown', handleKeyDown);
            window.removeEventListener('resize', handleResize);
            renderer.dispose();
        };
    }, [modelUrl]);

    return (
        <div>
            {showInstructions && (
                <div className="instructions" style={instructionStyle}>
                    <p>Left Click: Rotate the model</p>
                    <p>Right Click: Pan the model</p>
                    <p>W: Toggle Wireframe Mode</p>
                    <p>Space: Toggle Auto-Rotate</p>
                    <p>R: Reset Camera</p>
                </div>
            )}
            <div ref={containerRef} className="model-viewer" style={{ width: '100%', height: '100%' }} />
        </div>
    );
};

// Style for instructions
const instructionStyle = {
    position: 'absolute',
    top: '10px',
    left: '10px',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    padding: '10px',
    borderRadius: '5px',
    zIndex: 10,
    pointerEvents: 'none' // Prevent interaction with the instructions
};

export default ThreeDModelViewer;
