import React, { useEffect, useRef } from 'react';
import { Helmet } from 'react-helmet';
import * as THREE from 'three';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader.js';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './model.css';

const Model = () => {
  const mountRef = useRef(); // Reference for the Three.js renderer's mount point

  useEffect(() => {
    // Renderer setup
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;
    renderer.setClearColor(0x000000);

    if (mountRef.current) {
      mountRef.current.appendChild(renderer.domElement);
    }

    // Scene setup
    const scene = new THREE.Scene();

    // Camera setup
    const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 1, 1000);
    camera.position.set(0, 10, 20);

    // Controls setup
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.05;
    controls.enablePan = true;
    controls.enableZoom = true;
    controls.zoomSpeed = 1.0;
    controls.rotateSpeed = 0.8;
    controls.panSpeed = 0.8;
    controls.minDistance = 5;
    controls.maxDistance = 50;
    controls.minPolarAngle = 0;
    controls.maxPolarAngle = Math.PI;
    controls.autoRotate = false;
    controls.target.set(0, 0, 0);
    controls.update();

    // Lighting setup
    const spotLight = new THREE.SpotLight(0xffffff, 5000, 200, 0.5, 0.5);
    spotLight.position.set(10, 30, 10);
    spotLight.castShadow = true;
    scene.add(spotLight);

    const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
    scene.add(ambientLight);

    // Model loader
    let modelRef;
    const loader = new GLTFLoader().setPath('public/a_low_poly_house/');
    loader.load(
      'scene.gltf',
      (gltf) => {
        const mesh = gltf.scene;
        modelRef = mesh;

        // Center the model
        const box = new THREE.Box3().setFromObject(mesh);
        const center = box.getCenter(new THREE.Vector3());
        mesh.position.sub(center);

        mesh.traverse((child) => {
          if (child.isMesh) {
            child.castShadow = true;
            child.receiveShadow = true;
          }
        });

        scene.add(mesh);
        controls.update();
      },
      undefined,
      (error) => console.error(error)
    );

    // Resize handler
    const handleResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', handleResize);

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);
      controls.update();
      renderer.render(scene, camera);
    };
    animate();

    return () => {
      window.removeEventListener('resize', handleResize);
      mountRef.current.removeChild(renderer.domElement);
    };
  }, []);

  return (
    <div className="model-container">
      <Helmet>
        <title>Model - Mobillio Online Store</title>
        <meta property="og:title" content="Model - Mobillio Online Store" />
      </Helmet>
      <Navbar text="Back to Store" />
      <div className="model-viewer" ref={mountRef}></div>
      <Footer />
    </div>
  );
};

export default Model;
